import React from 'react';
import { Link } from 'react-router-dom';
import './HomePage.css'; // Make sure to add the CSS below

const HomePage = () => {
  return (
    <div className="home-container">
      {/* Welcome Section */}
      <header className="header">
        <div className="logo">
          <h1>MobileBazar</h1>
        </div>
      </header>

      {/* Main Section */}
      <section className="main-section">
        <div className="main-content">
          <h2>Welcome to MobileBazar</h2>
          <p>Buy and sell used mobile phones with ease</p>
          <div className="cta-buttons">
            <Link to="/login" className="cta-button login-btn">Login</Link>
            <Link to="/register" className="cta-button register-btn">Sign Up</Link>
          </div>
        </div>
      </section>

      {/* Footer Section */}
      <footer className="footer">
        <p>&copy; 2024 MobileBazar. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
