import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import AuthPage from "./components/AuthPage"; // Login/Registration page
import HomePage from "./components/HomePage"; // Home page after successful login
import BuyerPage from "./components/BuyerPage"; // Buyer page
import SellerPage from "./components/SellerPage"; // Seller page

function App() {
  return (
    <Router>
      <Switch>
        {/* Route for authentication (login/register) */}
        <Route path="/auth" exact component={AuthPage} />

        {/* Route for homepage after successful login */}
        <Route path="/" exact component={HomePage} />

        {/* Route for BuyerPage */}
        <Route path="/buyer" component={BuyerPage} />

        {/* Route for SellerPage */}
        <Route path="/seller" component={SellerPage} />
      </Switch>
    </Router>
  );
}

export default App;
